CREATE OR REPLACE PROCEDURE alterar_resp_credito_partic ( cd_pessoa_fisica_p text, nr_cirurgia_p bigint, ie_responsavel_credito_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
 
nr_seq_participante_w	bigint;
nr_seq_procedimento_w	bigint;					
 
C01 CURSOR FOR 
	SELECT 			nr_seq_partic, 
				nr_seq_procedimento 
	FROM			cirurgia_participante b, 
				procedimento_participante a 
	WHERE			b.nr_seq_procedimento		= a.nr_sequencia 
	and			b.cd_pessoa_fisica		= cd_pessoa_fisica_p 
	AND			b.nr_cirurgia			= nr_cirurgia_p;

	 

BEGIN 
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') and (ie_responsavel_credito_p IS NOT NULL AND ie_responsavel_credito_p::text <> '') then 
	 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_participante_w, 
		nr_seq_procedimento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin	 
		CALL Atualizar_Resp_Credito_Conta(nr_seq_procedimento_w,nr_seq_participante_w,cd_estabelecimento_p,ie_responsavel_credito_p, 'S',nm_usuario_p);		
		end;
	end loop;
	close C01;
	 
end if;
 
 
commit;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
