CREATE OR REPLACE PROCEDURE alterar_sequencia ( nr_prescricao_p bigint, nr_agrupamento_p bigint, nr_seq_material_p bigint, ie_item_superior_p text, nm_usuario_p text, nr_agrup_ant_p bigint, cd_perfil_p bigint, cd_estabelecimento_p text) AS $body$
DECLARE

 
ie_volta_horario_w	varchar(10) := '';
ie_item_superior_w	varchar(10) := ie_item_superior_p;
ds_horarios_w		varchar(50) := '';			
ie_recalcular_hora_w	varchar(1) := '';
ds_horarios_ww		varchar(4000) := '';

				 

BEGIN 
 
ie_recalcular_hora_w := Obter_Param_Usuario(7010, 106, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_recalcular_hora_w);
 
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_agrupamento_p IS NOT NULL AND nr_agrupamento_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then 
	begin 
	 
	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END  
	into STRICT	ie_volta_horario_w 
	from  prescr_material a 
	where	nr_prescricao	= nr_prescricao_p 
	and	nr_agrupamento	= nr_agrupamento_p 
	and	obter_classif_material_proced(a.cd_material, null,null) <> 1 
	and	coalesce(a.nr_sequencia_solucao::text, '') = '' 
	and	coalesce(a.nr_sequencia_proc::text, '') = '' 
	and	a.ie_agrupador	= 1 
	and	a.ie_suspenso	<> 'S' 
	and	coalesce(ie_administrar,'S') = 'S' 
	and	coalesce(a.nr_sequencia_diluicao::text, '') = '' 
	and   nr_sequencia <> nr_seq_material_p;
	 
	ie_item_superior_w := ajustar_item_superior_agrup(nr_prescricao_p, nr_seq_material_p, nr_agrupamento_p, ie_item_superior_w, 'F', '', nm_usuario_p);
				   
	update prescr_material 
	set  nr_agrupamento = nr_agrupamento_p 
	where nr_prescricao = nr_prescricao_p 
	and  nr_sequencia  = nr_seq_material_p;
		 
	--ie_recalcular_hora_w := 'S'; O par√¢metro que tem que controlar isto. OS 781604 
	 
	if (ie_recalcular_hora_w = 'S') then	 
	 
		ds_horarios_w := atualizar_horarios_agrup_medic( nr_prescricao_p, nr_seq_material_p, nr_agrupamento_p, nr_agrup_ant_p, 'F', '', '', ds_horarios_w, cd_estabelecimento_p, ie_volta_horario_w, nm_usuario_p);
	end if;
	 
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
