CREATE OR REPLACE PROCEDURE alterar_situacao_mat_marca (nr_sequencia_p bigint, cd_material_p bigint, ie_situacao_p text, ds_motivo_p text, nm_usuario_p text) AS $body$
DECLARE

ie_situacao_w 	varchar(1);
ds_motivo_w	varchar(32000);

BEGIN
ds_motivo_w := substr(ds_motivo_p,1,32000);

if (length(ds_motivo_w) > 255) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(181222);
end if;

if (ie_situacao_p = 'A') then
	ie_situacao_w := 'I';
else
	ie_situacao_w := 'A';
end if;

if (cd_material_p = 0) then
	update 	marca
	set 	ie_situacao = ie_situacao_w,
		ds_observacao = ds_motivo_w,
		nm_usuario = nm_usuario_p,
		nm_usuario_reprovacao = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where 	nr_sequencia = nr_sequencia_p;
end if;

update 	material_marca
set 	ie_situacao = ie_situacao_w,
	ds_observacao = ds_motivo_w,
	nm_usuario = nm_usuario_p,
	nm_usuario_reprovacao = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where 	nr_sequencia = nr_sequencia_p
and (cd_material = cd_material_p or cd_material_p = 0);

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
