CREATE OR REPLACE PROCEDURE alterar_situacao_operacao (nr_sequencia_p bigint, ie_situacao_p text) AS $body$
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	update	operacao_terceiro
	set	ie_situacao	= CASE WHEN coalesce(ie_situacao_p,'A')='A' THEN 'I'  ELSE 'A' END
	where	nr_sequencia	= nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
