SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_soluc_trat_onc ( nr_seq_paciente_p bigint, nr_seq_solucao_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w		smallint;
cd_material_w			bigint;
nr_agrupamento_w		bigint;
nr_seq_diluicao_w		bigint;
cd_material_diluido_w		bigint;
nr_seq_atendimento_w		bigint;
nr_seq_solucao_w		bigint;

c01 CURSOR FOR
	SELECT	*
	from	paciente_protocolo_soluc
	where	nr_seq_paciente		= nr_seq_paciente_p
	and	nr_seq_solucao		= nr_seq_solucao_p;

c01_w	c01%rowtype;

c02 CURSOR FOR
	SELECT	a.nr_seq_atendimento,
		b.nr_seq_solucao
	from	paciente_atendimento a,
		paciente_atend_soluc b
	where	a.nr_seq_atendimento	= b.nr_seq_atendimento
	and	a.nr_seq_paciente	= nr_seq_paciente_p
	and	b.nr_agrupamento	= nr_agrupamento_w;


BEGIN
select	coalesce(max(b.cd_estabelecimento),1)
into STRICT	cd_estabelecimento_w
from	paciente_setor		b
where	b.nr_seq_paciente = nr_seq_paciente_p;

CALL wheb_usuario_pck.set_ie_executar_trigger('N');

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_agrupamento_w	:= c01_w.nr_agrupamento;

	open C02;
	loop
	fetch C02 into
		nr_seq_atendimento_w,
		nr_seq_solucao_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		update	paciente_atend_soluc
		set	IE_BOMBA_INFUSAO	= c01_w.IE_BOMBA_INFUSAO,
			IE_ESQUEMA_ALTERNADO	= c01_w.IE_ESQUEMA_ALTERNADO,
			IE_CALC_AUT		= c01_w.IE_CALC_AUT,
			IE_ACM			= c01_w.IE_ACM,
			NR_ETAPAS		= c01_w.NR_ETAPAS,
			QT_HORA_FASE		= c01_w.QT_HORA_FASE,
			QT_TEMPO_APLICACAO	= c01_w.QT_TEMPO_APLICACAO,
			IE_SE_NECESSARIO	= c01_w.IE_SE_NECESSARIO,
			IE_SOLUCAO_PCA		= c01_w.IE_SOLUCAO_PCA,
			QT_SOLUCAO_TOTAL	= c01_w.QT_SOLUCAO_TOTAL,
			QT_DOSAGEM		= c01_w.QT_DOSAGEM,
			IE_VIA_APLICACAO	= c01_w.IE_VIA_APLICACAO,
			CD_INTERVALO		= c01_w.CD_INTERVALO,
			IE_TIPO_DOSAGEM		= c01_w.IE_TIPO_DOSAGEM,
			DS_SOLUCAO		= c01_w.DS_SOLUCAO,
			DS_ORIENTACAO		= c01_w.DS_ORIENTACAO,
			IE_TIPO_SOL		= c01_w.IE_TIPO_SOL,
			IE_TIPO_ANALGESIA	= c01_w.IE_TIPO_ANALGESIA,
			IE_PCA_MODO_PROG	= c01_w.IE_PCA_MODO_PROG,
			QT_VOL_INFUSAO_PCA	= c01_w.QT_VOL_INFUSAO_PCA,
			QT_BOLUS_PCA		= c01_w.QT_BOLUS_PCA,
			QT_INTERVALO_BLOQUEIO	= c01_w.QT_INTERVALO_BLOQUEIO,
			QT_LIMITE_QUATRO_HORA	= c01_w.QT_LIMITE_QUATRO_HORA,
			QT_DOSE_INICIAL_PCA	= c01_w.QT_DOSE_INICIAL_PCA,
			QT_DOSE_ATAQUE		= c01_w.QT_DOSE_ATAQUE,
			IE_PRE_MEDICACAO	= c01_w.IE_PRE_MEDICACAO,
			PR_REDUCAO		= c01_w.PR_REDUCAO
		where	nr_seq_atendimento	= nr_seq_atendimento_w
		and	nr_seq_solucao		= nr_seq_solucao_w;

		end;

	end loop;
	close C02;

	end;
end loop;
close C01;

CALL wheb_usuario_pck.set_ie_executar_trigger('S');
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
