CREATE OR REPLACE PROCEDURE alterar_status_agendas_agepac ( cd_agenda_p bigint, ie_status_p text, ie_agenda_dia_p text, ds_lista_seq_agendas_p text, nm_usuario_p text) AS $body$
DECLARE

 
ds_lista_seq_agendas_w	varchar(4000);
nr_pos_virgula_w	bigint;
nr_seq_agenda_w		bigint;
nr_ctrl_loop_w		smallint := 0;

BEGIN
 
if (ds_lista_seq_agendas_p IS NOT NULL AND ds_lista_seq_agendas_p::text <> '') and (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (ie_status_p IS NOT NULL AND ie_status_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	ds_lista_seq_agendas_w	:= ds_lista_seq_agendas_p;
	 
	while(ds_lista_seq_agendas_w IS NOT NULL AND ds_lista_seq_agendas_w::text <> '') and (nr_ctrl_loop_w < 100) loop 
		begin 
		nr_ctrl_loop_w 		:= nr_ctrl_loop_w + 1;
		nr_pos_virgula_w	:= position(',' in ds_lista_seq_agendas_w);
		 
		if (coalesce(nr_pos_virgula_w,0) > 0) then 
			begin 
			nr_seq_agenda_w		:= substr(ds_lista_seq_agendas_w,0,nr_pos_virgula_w-1);
			ds_lista_seq_agendas_w	:= substr(ds_lista_seq_agendas_w,nr_pos_virgula_w+1,length(ds_lista_seq_agendas_w));
			end;
		else 
			begin 
			nr_seq_agenda_w		:= ds_lista_seq_agendas_w;
			ds_lista_seq_agendas_w	:= null;
			end;
		end if;
		 
		if (coalesce(nr_seq_agenda_w,0) > 0) then 
			begin 
			CALL alterar_status_agenda( 
