SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_status_agenda_agecons ( nr_sequencia_p bigint, nr_seq_status_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_status_agenda_w		varchar(3);
ie_status_agenda_old_w		varchar(3);


BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (coalesce(nr_sequencia_p, 0) > 0) then

	select max(ie_status_agenda) ie_status_agenda_old
	into STRICT	ie_status_agenda_old_w
	from	agenda_consulta
	where 	nr_sequencia =  nr_sequencia_p;

	select	max(ie_status_agenda) ie_status_agenda
	into STRICT	ie_status_agenda_w
	from	agenda_cons_status_adic
	where	ie_situacao = 'A'
	and	nr_sequencia = nr_seq_status_p;

	if (ie_status_agenda_old_w = 'S') and (ie_status_agenda_w = 'S') then
		ie_status_agenda_w := obter_penultimo_status(nr_sequencia_p);
	end if;

	if (ie_status_agenda_w IS NOT NULL AND ie_status_agenda_w::text <> '') then
		update	agenda_consulta
		set	ie_status_agenda  = ie_status_agenda_w,
			dt_atualizacao    = clock_timestamp(),
			nm_usuario        = nm_usuario_p
		where	nr_sequencia      = nr_sequencia_p;
	end if;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
