CREATE OR REPLACE PROCEDURE alterar_status_agenda_hc ( nr_seq_agenda_p bigint, ie_acao_p text, nm_usuario_p text, ds_motivo_canc_p text, cd_funcionario_p text default null, nr_lista_proc_p text default null) AS $body$
DECLARE

 
ie_status_agenda_w	varchar(15);
ie_excluir_proc_w	varchar(1);
ie_exec_sem_prof_w	varchar(1);
qt_resp_w		bigint;

BEGIN
 
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then 
 
	ie_excluir_proc_w := obter_param_usuario(867, 49, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_excluir_proc_w);
	ie_exec_sem_prof_w := obter_param_usuario(867, 60, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_exec_sem_prof_w);
	 
	if (ie_exec_sem_prof_w = 'N') then 
	 
		select	count(*) 
		into STRICT	qt_resp_w 
		from	agenda_hc_paciente c, 
			hc_agenda_prof a 
		where	a.nr_seq_agenda = c.nr_sequencia 
		and	c.nr_sequencia = nr_seq_agenda_p;
		 
		if (qt_resp_w = 0) then 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(227319);
		end if;
	end if;
	 
	select	max(ie_status_agenda) 
	into STRICT	ie_status_agenda_w 
	from	agenda_hc_paciente 
	where	nr_sequencia = nr_seq_agenda_p;
 
	if (ie_acao_p = 'E') and (coalesce(ie_status_agenda_w, 'N') <> 'E') then 
		CALL lancar_procedimento_conta_hc(nr_seq_agenda_p, nm_usuario_p, cd_funcionario_p, nr_lista_proc_p);
	end if;
	 
	if (ie_excluir_proc_w = 'S') and (ie_acao_p = 'C') and (ie_status_agenda_w = 'E') then 
		CALL hc_desfazer_proc_conta(nr_seq_agenda_p,nm_usuario_p);
	end if;
	 
	update	agenda_hc_paciente 
	set	ie_status_agenda	= ie_acao_p, 
		dt_Atualizacao		= clock_timestamp(), 
		ds_motivo_canc	=	ds_motivo_canc_p, 
		nm_usuario		= nm_usuario_p 
	where	nr_sequencia		= nr_seq_agenda_p;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
