CREATE OR REPLACE PROCEDURE alterar_status_agenda_html (cd_agenda_p bigint, nr_seq_agenda_p bigint, ie_status_p text, cd_motivo_p text, ds_motivo_p text, ie_agenda_dia_p text, nm_usuario_p text, ie_bloquear_p text) AS $body$
DECLARE


ie_bloquear_w		varchar(3) := 'N';
ie_status_agenda_w	varchar(3);


BEGIN

	ie_bloquear_w := coalesce(ie_bloquear_p,'N');

	select	Max(ie_status_agenda)
	into STRICT	ie_status_agenda_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_agenda_p;

	if (ie_bloquear_w = 'S' AND ie_status_agenda_w = 'L') then
		CALL alterar_status_agenda(cd_agenda_p, nr_seq_agenda_p, ie_status_p, cd_motivo_p, ds_motivo_p,	ie_agenda_dia_p, nm_usuario_p);	
	elsif (ie_bloquear_w = 'N' AND ie_status_agenda_w = 'B') then
		CALL alterar_status_agenda(cd_agenda_p, nr_seq_agenda_p, ie_status_p, cd_motivo_p, ds_motivo_p,	ie_agenda_dia_p, nm_usuario_p);	
	end if;

end;
$body$
