SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_status_agenda_pep (nr_seq_agenda_p bigint, ie_status_agenda_p text, nm_usuario_p text) AS $body$
DECLARE


ie_status_valido_w	varchar(1);
nr_atendimento_w	bigint;
dt_atend_medico_w	timestamp;


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (ie_status_agenda_p IS NOT NULL AND ie_status_agenda_p::text <> '') then
	/* validar dominio */

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_status_valido_w
	from	valor_dominio_v
	where	cd_dominio = 83
	and	vl_dominio = ie_status_agenda_p;
	
	if (ie_status_valido_w = 'S') then
		update	agenda_consulta
		set	ie_status_agenda = ie_status_agenda_p,
			nm_usuario	 = nm_usuario_p
		where	nr_sequencia = nr_seq_agenda_p;
		
		if (ie_status_agenda_p = 'AC') then
		
			select	coalesce(max(nr_atendimento),0)
			into STRICT	nr_atendimento_w
			from	agenda_consulta
			where	nr_sequencia = nr_seq_agenda_p;
			
			if (nr_atendimento_w > 0) then			
			
				select 	max(DT_ATEND_MEDICO)
				into STRICT	dt_atend_medico_w
				from	atendimento_paciente
				where	nr_atendimento = nr_atendimento_w;
				
				if (dt_atend_medico_w IS NOT NULL AND dt_atend_medico_w::text <> '') then
				
					update 	atendimento_paciente
					set	dt_atend_medico  = NULL
					where	nr_atendimento = nr_atendimento_w;
					
				end if;
				
			end if;
		
		elsif (ie_status_agenda_p = 'E') then

			CALL agend_executado_list_esp(nr_seq_agenda_p, nm_usuario_p);
		
		end if;
		
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
