SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_status_alerta_pf (nr_seq_alerta_p bigint, ie_status_p text) AS $body$
BEGIN
if (nr_seq_alerta_p IS NOT NULL AND nr_seq_alerta_p::text <> '') and (ie_status_p IS NOT NULL AND ie_status_p::text <> '') then

	if (ie_status_p = 'L') then /* liberar */
		update	pf_alerta
		set		dt_liberacao	= clock_timestamp()
		where	nr_sequencia	= nr_seq_alerta_p;

	elsif (ie_status_p = 'C') then /* ciente */
		update	pf_alerta
		set		dt_ciencia	= clock_timestamp()
		where	nr_sequencia	= nr_seq_alerta_p;

	elsif (ie_status_p = 'R') then /* Resolução*/
		update  pf_alerta
		set	 	dt_resolucao = clock_timestamp()
		where	nr_sequencia = nr_seq_alerta_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE alterar_status_alerta_pf (nr_seq_alerta_p bigint, ie_status_p text) FROM PUBLIC;

