CREATE OR REPLACE PROCEDURE alterar_status_aprov_result ( nr_prescricao_p bigint, ie_status_atual_p bigint, ie_consiste_lib_p text, ds_sql_motivo_alt_p text, ds_sql_param_p text, ds_sql_amostra_p text, ds_sql_item_p text, ie_restringir_p text, nm_usuario_p text, ie_exame_aprov_p text, ie_aprovar_nao_digit_p text, ie_exame_amostra_p text, qt_exame_etapa_amost_p bigint, nr_seq_motivo_atraso_p bigint, ds_mensagem_p INOUT text, ie_atualizar_status_p INOUT text) AS $body$
DECLARE


qt_exames_sem_result_w	bigint;
ds_comando_update_w	varchar(4000);
ds_sep_bv_w		varchar(50);


BEGIN

ds_sep_bv_w	:= obter_separador_bv;

if (ie_consiste_lib_p = 'S') then

	select 	count(*)
	into STRICT 	qt_exames_sem_result_w
	from 	prescr_procedimento c
	where 	nr_prescricao 	= nr_prescricao_p
	and 	ie_status_atend = ie_status_atual_p
	and 	not exists (	SELECT	1
				from 	result_laboratorio x
				where 	c.nr_prescricao = x.nr_prescricao
				and 	c.nr_sequencia = x.nr_seq_prescricao)
	and 	(((ie_restringir_p = 'S') and (obter_se_contido(nr_sequencia,ds_sql_param_p) = 'S')) or (ie_restringir_p = 'N'));

	if (qt_exames_sem_result_w > 0) then
		ds_mensagem_p := obter_desc_expressao(624680);
	end if;
end if;

if (ie_exame_aprov_p = 'S') then

	ds_comando_update_w :=  ' update	prescr_procedimento c ' ||
				' set 		ie_status_atend 	= 35, ' ||
				'     		nm_usuario 		= :nm_usuario,' ||
				'     		dt_atualizacao 		= sysdate ' ||ds_sql_amostra_p || ds_sql_motivo_alt_p ||
				' where 	nr_prescricao 		= :nr_prescricao '||
				' and   	lab_obter_se_digit_prescr(:nr_prescricao, c.nr_sequencia, :ie_opcao_p) = ''S'' '||
				' and 		ie_status_atend 	= :ie_status_atual ' || ds_sql_item_p;
	if (ie_consiste_lib_p = 'S') then
		ds_comando_update_w := ds_comando_update_w ||  ' and (exists(	select	1  ' ||
								' 		from 	result_laboratorio x '  ||
								' 		where 	c.nr_prescricao = x.nr_prescricao ' ||
								' 		and 	c.nr_sequencia = x.nr_seq_prescricao)) ';
	end if;

	if (ie_aprovar_nao_digit_p = 'N') or (ie_aprovar_nao_digit_p = 'SM') then

		CALL exec_sql_dinamico_bv('', ds_comando_update_w, 	'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
								'nr_prescricao=' || to_char(nr_prescricao_P) || ds_sep_bv_w ||
								'ie_status_atual=' || to_char(ie_status_atual_p) || ds_sep_bv_w ||
								'ie_opcao_p=1' || ds_sep_bv_w);
		commit;

	elsif (ie_aprovar_nao_digit_p = 'NA') then

		CALL exec_sql_dinamico_bv('', ds_comando_update_w, 	'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
								'nr_prescricao=' || to_char(nr_prescricao_P) || ds_sep_bv_w ||
								'ie_status_atual=' || to_char(ie_status_atual_p) || ds_sep_bv_w ||
								'ie_opcao_p=2' || ds_sep_bv_w);
		commit;

	elsif (ie_aprovar_nao_digit_p = 'ND') then

		CALL exec_sql_dinamico_bv('', ds_comando_update_w, 	'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
								'nr_prescricao=' || to_char(nr_prescricao_P) || ds_sep_bv_w ||
								'ie_status_atual=' || to_char(ie_status_atual_p) || ds_sep_bv_w ||
								'ie_opcao_p=3' || ds_sep_bv_w);
		commit;

	else
		if (ie_exame_amostra_p = 'S') and (qt_exame_etapa_amost_p > 0) then
			ie_atualizar_status_p := 'S';
		else

			ds_comando_update_w :=	' update	prescr_procedimento c ' ||
						' set 		ie_status_atend = 35, ' ||
						' 		nm_usuario = :nm_usuario, ' ||
						' 		dt_atualizacao = sysdate ' || ds_sql_amostra_p || ds_sql_motivo_alt_p ||
						' where 	nr_prescricao = :nr_prescricao '||
						' and 		ie_status_atend = :ie_status_atual ' || ds_sql_item_p;

			if (ie_consiste_lib_p = 'S') then
				ds_comando_update_w := ds_comando_update_w  || ' and (exists(	select	1  ' ||
						'						from 	result_laboratorio x '  ||
						'						where 	c.nr_prescricao = x.nr_prescricao ' ||
						'						and 	c.nr_sequencia = x.nr_seq_prescricao)) ';
			end if;

			CALL exec_sql_dinamico_bv('', ds_comando_update_w, 	'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
									'nr_prescricao=' || to_char(nr_prescricao_P) || ds_sep_bv_w ||
									'ie_status_atual=' || to_char(ie_status_atual_p) || ds_sep_bv_w);

			commit;

		end if;
	end if;

	CALL lab_registra_motivo_atraso(nr_prescricao_p, nr_seq_motivo_atraso_p, ds_sql_item_p, nm_usuario_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
