DECLARE


nr_seq_status_w		bigint;
nr_seq_atend_futuro_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_status_w
from   	status_atend_futuro
where  	ie_status = 5;

