CREATE OR REPLACE PROCEDURE alterar_status_digit_result ( nr_prescricao_p bigint, ie_status_atual_p bigint, ds_sql_motivo_alt_p text, ds_sql_param_p text, nm_usuario_p text, ie_restringir_p text, ie_alterar_status_p text, nr_seq_motivo_atraso_p bigint) AS $body$
DECLARE



nr_seq_resultado_w	bigint;
qt_exames_dig_w		bigint;
ds_comando_update_w	varchar(4000);
ds_sep_bv_w		varchar(50);


BEGIN

ds_sep_bv_w	:= obter_separador_bv;

if (ie_alterar_status_p = 'S') then

	ds_comando_update_w 	:= 	' update	prescr_procedimento ' ||
					' set 		ie_status_atend = 30, ' ||
					' 		nm_usuario = :nm_usuario, ' ||
					' 		dt_atualizacao = sysdate ' || ds_sql_motivo_alt_p ||
					' where		nr_prescricao = :nr_prescricao ' ||
					' and 		ie_status_atend = :ie_status_atual ';

	if (ie_restringir_p = 'S') then
		ds_comando_update_w 	:= ds_comando_update_w || ' and nr_sequencia in ('||ds_sql_param_p||') ';
	end if;

	CALL exec_sql_dinamico_bv('', ds_comando_update_w, 	'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
							'nr_prescricao=' || to_char(nr_prescricao_P) || ds_sep_bv_w ||
							'ie_status_atual=' || to_char(ie_status_atual_p) || ds_sep_bv_w);

	CALL lab_registra_motivo_atraso(nr_prescricao_p, nr_seq_motivo_atraso_p,' and c.nr_sequencia in ('||ds_sql_param_p||') ', nm_usuario_p);
end if;

select	count(*)
into STRICT	qt_exames_dig_w
from    prescr_procedimento
where	nr_prescricao	= nr_prescricao_p
and	ie_status_atend	= 30;

select 	max(nr_seq_resultado)
into STRICT	nr_seq_resultado_w
from 	exame_lab_resultado
where 	nr_prescricao 	= nr_prescricao_p;

if (nr_seq_resultado_w > 0) and (qt_exames_dig_w = 0) then

	update	exame_lab_resultado
	set 	dt_resultado = clock_timestamp()
	where 	nr_seq_resultado = nr_seq_resultado_w;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
