CREATE OR REPLACE PROCEDURE alterar_status_disp_sae ( nr_seq_dispositivo_p bigint, ie_opcao_p text, dt_termino_p timestamp default null, ds_justificativa_p text DEFAULT NULL, dt_validade_prescr_p timestamp default null) AS $body$
DECLARE


nm_usuario_w	varchar(15);


BEGIN

if (nr_seq_dispositivo_p IS NOT NULL AND nr_seq_dispositivo_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then

	nm_usuario_w := wheb_usuario_pck.get_nm_usuario;

	if (ie_opcao_p = 'R') and (dt_validade_prescr_p IS NOT NULL AND dt_validade_prescr_p::text <> '') and (dt_termino_p > dt_validade_prescr_p)  then

		CALL Wheb_mensagem_pck.exibir_mensagem_abort(296401);

	end if;

	if	((ie_opcao_p = 'E') or (ie_opcao_p = 'R')) and (dt_termino_p IS NOT NULL AND dt_termino_p::text <> '') and (ds_justificativa_p IS NOT NULL AND ds_justificativa_p::text <> '') then

		CALL alterar_horario_disp(nr_seq_dispositivo_p,dt_termino_p,ds_justificativa_p,nm_usuario_w);

	end if;

	Update	atend_pac_dispositivo
	set		ie_acao_sae = ie_opcao_p
	where	nr_sequencia = nr_seq_dispositivo_p;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
