CREATE OR REPLACE PROCEDURE alterar_status_evento ( nr_sequencia_p bigint, ie_status_p text, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_sequencia_p > 0) then
	update	ev_evento_pac_destino
	set	nm_usuario		=	nm_usuario_p,
		dt_atualizacao		=	clock_timestamp(),
		ie_status 		=	ie_status_p,
		dt_ciencia		=	clock_timestamp()
	where	nr_seq_ev_pac		=	nr_sequencia_p
	and	cd_pessoa_fisica	=	cd_pessoa_fisica_p
	and	coalesce(dt_ciencia::text, '') = '';

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
