CREATE OR REPLACE PROCEDURE alterar_status_exame_cons ( cd_pessoa_fisica_p text, nm_usuario_p text, ie_exame_p text, ie_opcao_p text) AS $body$
BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (ie_exame_p IS NOT NULL AND ie_exame_p::text <> '') then

	if (ie_opcao_p = 'R') then
		begin
			update	med_cliente
			set	ie_exame_virtual	= ie_exame_p
			where	cd_pessoa_fisica	= cd_pessoa_fisica_p
			and 	nm_usuario 			= nm_usuario_p;
		commit;
		end;
	elsif (ie_opcao_p = 'E')	then
		update	med_cliente
		set	ie_exame_consultorio	= ie_exame_p
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p
			and 	nm_usuario 			= nm_usuario_p;
		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
