CREATE OR REPLACE PROCEDURE alterar_status_item_processo (nr_seq_processo_p bigint, nr_seq_horario_p bigint, ie_status_p text, nm_usuario_p text) AS $body$
BEGIN
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') and (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') and (ie_status_p IS NOT NULL AND ie_status_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	update	prescr_mat_hor
	set	dt_suspensao	= CASE WHEN ie_status_p='S' THEN  clock_timestamp()  ELSE null END ,
		nm_usuario_susp	= CASE WHEN ie_status_p='S' THEN  nm_usuario_p  ELSE null END
	where	nr_sequencia	= nr_seq_horario_p;

end if;

commit;

end;
$body$
