CREATE OR REPLACE PROCEDURE alterar_status_leito_transf ( ie_status_unidade_p text, nm_usuario_p text, cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text) AS $body$
BEGIN

if (coalesce(cd_setor_atendimento_p,0) > 0) and (ie_status_unidade_p IS NOT NULL AND ie_status_unidade_p::text <> '') then

