CREATE OR REPLACE PROCEDURE alterar_status_lista_espera (nm_usuario_p text, nr_seq_lista_p bigint, ie_status_espera_p text, ie_cancela_agenda_p text) AS $body$
BEGIN
if (nr_seq_lista_p IS NOT NULL AND nr_seq_lista_p::text <> '') then 
 
 
	update 	agenda_lista_espera 
	set		ie_status_espera 	= 	ie_status_espera_p, 
			dt_atualizacao 		=	clock_timestamp(), 
			nm_usuario			=	nm_usuario_p 
	where	nr_sequencia		=	nr_seq_lista_p;
	 
	if (ie_cancela_agenda_p = 'S')	then 
	 
	update		agenda_paciente 
	set			dt_atualizacao 	= 	clock_timestamp(), 
				nm_usuario		=	nm_usuario_p, 
				nr_seq_lista	 = NULL 
	where		nr_seq_lista	=	nr_seq_lista_p;
	 
	end if;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
