CREATE OR REPLACE PROCEDURE alterar_status_lista_esp_mut ( nr_seq_lista_p bigint, ie_status_espera_p text, nm_usuario_p text) AS $body$
DECLARE


qtd_agendamento_w		agenda_integrada_item.nr_seq_agenda_cons%type;
			

BEGIN

if (nr_seq_lista_p IS NOT NULL AND nr_seq_lista_p::text <> '') then
	
	select 	max(a.nr_seq_agenda_cons)
	into STRICT 	qtd_agendamento_w
	from 	agenda_integrada_item a
	where 	nr_seq_lista_espera = nr_seq_lista_p;
	
	if (coalesce( qtd_agendamento_w, 0) > 0 ) then
		update 	agenda_lista_espera
		set		ie_status_espera  	= 	ie_status_espera_p,
				dt_atualizacao 		=	clock_timestamp(),
				nm_usuario			=	nm_usuario_p
		where	nr_sequencia		=	nr_seq_lista_p;
	end if;	

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
