CREATE OR REPLACE PROCEDURE alterar_status_proc_mat_repass ( ie_proc_mat_p bigint, nr_sequencia_p text, ie_status_alt_p text, ie_status_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_proc_mat_p = 0) then
	update	procedimento_repasse
	set	ie_status	= ie_status_alt_p,
		vl_liberado	= 0,
		dt_liberacao	 = NULL,
		nm_usuario	= nm_usuario_p,
		nr_seq_ret_glosa	 = NULL,
		nr_seq_item_retorno	 = NULL,
		nm_usuario_lib		 = NULL
	where	nr_sequencia = nr_sequencia_p
	and	coalesce(nr_repasse_terceiro::text, '') = '';
end if;

if (ie_proc_mat_p = 1) then
	update	material_repasse
	set	ie_status	= ie_status_alt_p,
		vl_liberado	= 0,
		dt_liberacao	 = NULL,
		nm_usuario	= nm_usuario_p,
		nr_seq_ret_glosa	 = NULL,
		nr_seq_item_retorno	 = NULL,
		nm_usuario_lib		 = NULL
	where	nr_sequencia	= nr_sequencia_p
	and	coalesce(nr_repasse_terceiro::text, '') = '';
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
