CREATE OR REPLACE PROCEDURE alterar_status_protocolo ( nr_seq_protocolo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



qt_registro_w			bigint;
qt_registro_ww			bigint;
qt_registro_estagio_w		bigint;
qt_registro_estagio_ww		bigint;
nr_seq_estagio_w		bigint;
nr_seq_estagio_ww		bigint;


BEGIN

nr_seq_estagio_w := Obter_Param_Usuario(10025, 6, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, nr_seq_estagio_w);
nr_seq_estagio_ww := Obter_Param_Usuario(10025, 14, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, nr_seq_estagio_ww);

if (nr_seq_estagio_w IS NOT NULL AND nr_seq_estagio_w::text <> '') then
	if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then
		select count(*)
		into STRICT   qt_registro_w
		from   lote_producao_comp a
		where  a.nr_seq_protocolo = nr_seq_protocolo_p;
	end if;

	if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') and (nr_seq_estagio_w IS NOT NULL AND nr_seq_estagio_w::text <> '') then
		select count(*)
		into STRICT   qt_registro_estagio_w
		from   lote_producao_comp a
		where  a.nr_seq_protocolo = nr_seq_protocolo_p
		and    a.nr_seq_estagio = nr_seq_estagio_w;

	end if;

	if (qt_registro_w = qt_registro_estagio_w) then
		UPDATE protocolo_fornec_opme
		SET    ie_status = 'T'
		WHERE  nr_sequencia = nr_seq_protocolo_p;



	commit;

	end if;
end if;

if (nr_seq_estagio_ww IS NOT NULL AND nr_seq_estagio_ww::text <> '') then
	if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then
		select count(*)
		into STRICT   qt_registro_ww
		from   lote_producao_comp a
		where  a.nr_seq_protocolo = nr_seq_protocolo_p;
	end if;

	if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') and (nr_seq_estagio_ww IS NOT NULL AND nr_seq_estagio_ww::text <> '') then
		select count(*)
		into STRICT   qt_registro_estagio_ww
		from   lote_producao_comp a
		where  a.nr_seq_protocolo = nr_seq_protocolo_p
		and    a.nr_seq_estagio = nr_seq_estagio_ww;

	end if;

	if (qt_registro_ww = qt_registro_estagio_ww) then
		UPDATE protocolo_fornec_opme
		SET    ie_status = 'F'
		WHERE  nr_sequencia = nr_seq_protocolo_p;

	commit;

	end if;


end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
