SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_status_repeticao_js ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_confirmacao_p text, nm_usuario_p text) AS $body$
DECLARE


ie_status_repet_w	lab_parametro.ie_status_repeticao%type;


BEGIN

select 	coalesce(max(a.ie_status_repeticao),0)
into STRICT	ie_status_repet_w
from	lab_parametro a, prescr_medica b
where   b.nr_prescricao = nr_prescricao_p
and 	a.cd_estabelecimento = b.cd_estabelecimento;


if (ie_confirmacao_p = 'N') then

	update 	prescr_procedimento
	set    	ie_exame_bloqueado = 'R',
		nm_usuario = nm_usuario_p,
		ie_status_atend	= CASE WHEN ie_status_repet_w=0 THEN ie_status_atend  ELSE ie_status_repet_w END
	where  	nr_sequencia = nr_seq_prescr_p
	and    	nr_prescricao = nr_prescricao_p
	and    ((ie_status_repet_w = 0) or (ie_status_atend < 35));
	commit;

	CALL Insere_usuario_repeticao(nr_prescricao_p, nr_seq_prescr_p, nm_usuario_p);

	if (ie_status_repet_w > 0) then
		CALL Lab_atualiza_exame_lab_rep(nr_prescricao_p, nr_seq_prescr_p, nm_usuario_p);
	end if;

else

	update 	prescr_procedimento
	set    	ie_exame_bloqueado = 'N',
		nm_usuario = nm_usuario_p
	where  	nr_sequencia = nr_seq_prescr_p
	and    	nr_prescricao = nr_prescricao_p;
	commit;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
