CREATE OR REPLACE PROCEDURE alterar_status_rxt_agenda ( ds_status_p text, nm_usuario_p text, cd_agendamento_externo_p text, dt_agenda_p timestamp, nr_minuto_duracao_p integer, evento_disparado_p text, cd_equip_externo_p text, cd_pessoa_fisica_p text, codigo_mensagem_p text) AS $body$
DECLARE


nr_seq_tratamento_w bigint;
ie_status_w varchar(100);


BEGIN

select max(b.nr_sequencia)
into STRICT nr_seq_tratamento_w
from rxt_tratamento b,
	rxt_agenda c
where b.nr_Sequencia = c.nr_Seq_tratamento
and c.cd_agendamento_externo = cd_agendamento_externo_p;

if ('S14' = evento_disparado_p) then /* Notification of Appointment Modification */
	
	if (ds_status_p = 'STARTED') then
		ie_status_w := 'T';
	elsif (ds_status_p = 'COMPLETE') then	
		ie_status_w := 'E';
	end if;

update rxt_agenda
	set dt_agenda = dt_agenda_p,
	nr_minuto_duracao = nr_minuto_duracao_p,
	ie_status_agenda = coalesce(ie_status_w, ie_status_agenda)
	where cd_agendamento_externo = cd_agendamento_externo_p;

elsif ('S15' = evento_disparado_p)then /* Notification of Appointment Cancellation*/
	update rxt_agenda
	set ie_status_agenda = 'C',
	dt_cancelamento = clock_timestamp(),
	nm_usuario_cancel = nm_usuario_p
	where cd_agendamento_externo = cd_agendamento_externo_p;
	
elsif ('S26' = evento_disparado_p) then /* Notification of appointment discontinuation */
	update rxt_agenda
	set ie_status_agenda = 'C',
	dt_cancelamento = clock_timestamp(),
	nm_usuario_cancel = nm_usuario_p
	where nr_seq_tratamento = nr_seq_tratamento_w
	and ie_status_agenda in ('A');
	
	CALL inserir_rxt_agenda_integracao(
		cd_pessoa_fisica_p,
		cd_agendamento_externo_p,
		dt_agenda_p,
		nr_minuto_duracao_p,
		cd_equip_externo_p,
		nm_usuario_p,
		evento_disparado_p,
		codigo_mensagem_p);

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
