CREATE OR REPLACE PROCEDURE alterar_status_servico (nr_sequencia_p bigint) AS $body$
DECLARE


ie_status_w	varchar(15);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	ie_status
	into STRICT	ie_status_w
	from	agenda_pac_servico
