SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_status_titulo_pagar ( nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Alterar o status do título a pagar
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/BEGIN
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	update	titulo_pagar
	set	dt_liberacao	= CASE WHEN ie_status='D' THEN null  ELSE clock_timestamp() END ,
		nm_usuario_lib	= CASE WHEN ie_status='D' THEN null  ELSE nm_usuario_p END ,
		ie_status	= CASE WHEN ie_status='D' THEN 'P'  ELSE 'D' END
	where	nr_titulo	= nr_titulo_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
