CREATE OR REPLACE PROCEDURE alterar_stat_acerto_conta_pac (nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_ctb_online_w varchar(1) := 'N';
cd_estabelecimento_w estabelecimento.cd_estabelecimento%type;
nr_lote_contabil_w lote_contabil.nr_lote_contabil%type;


BEGIN

begin
select cd_estabelecimento,
       nr_lote_contabil
  into STRICT cd_estabelecimento_w,
       nr_lote_contabil_w
  from conta_paciente
 where nr_interno_conta = nr_interno_conta_p;
exception
  when others then
    cd_estabelecimento_w := null;
	nr_lote_contabil_w := null;
end;

ie_ctb_online_w := ctb_online_pck.get_modo_lote( 6, cd_estabelecimento_w);

if (nr_lote_contabil_w IS NOT NULL AND nr_lote_contabil_w::text <> '') and ie_ctb_online_w = 'S' then

 CALL ctb_contab_onl_lote_receita(nr_seq_protocolo_p => null,
                             nr_interno_conta_p => nr_interno_conta_p,
                             nm_usuario_p       => nm_usuario_p,
							 ie_operacao_p      => 2);

end if;

update	conta_paciente
set	ie_status_acerto = 1,
	dt_atualizacao = clock_timestamp(),
	dt_geracao_tiss  = NULL,
	nm_usuario = nm_usuario_p
where	nr_interno_conta = nr_interno_conta_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
