SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_submotivo_alta ( nr_atendimento_p bigint, cd_submotivo_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_submotivo_w			varchar(255);
ds_submotivo_ant_w		varchar(255);
nr_sequencia_w			bigint;

BEGIN
 
if (coalesce(nr_atendimento_p,0) > 0) and (coalesce(cd_submotivo_p,0) > 0) then 
	 
	select	coalesce(obter_desc_submotivo_alta(max(nr_submotivo_alta)),'') 
	into STRICT	ds_submotivo_ant_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
	select	coalesce(obter_desc_submotivo_alta(cd_submotivo_p),'') 
	into STRICT	ds_submotivo_w 
	;
	 
	update	atendimento_paciente 
	set	nr_submotivo_alta = cd_submotivo_p, 
		dt_atualizacao = clock_timestamp(), 
		nm_usuario = nm_usuario_p 
	where	nr_atendimento = nr_atendimento_p;
	 
	insert into	atendimento_hist_paciente(	nr_sequencia, 
							dt_atualizacao, 
							nm_usuario, 
							dt_historico, 
							nr_atendimento, 
							dt_atualizacao_nrec, 
							nm_usuario_nrec, 
							nr_seq_tipo_historico, 
							ds_observacao, 
							dt_liberacao, 
							dt_inativacao, 
							nm_usuario_inativacao, 
							nm_usuario_liberacao) 
	values (	nextval('atendimento_hist_paciente_seq'), 
							clock_timestamp(), 
							nm_usuario_p, 
							clock_timestamp(), 
							nr_atendimento_p, 
							clock_timestamp(), 
							nm_usuario_p, 
							null, 
							wheb_mensagem_pck.get_texto(794852) ||' ' || ds_submotivo_ant_w || chr(10) || chr(13) || wheb_mensagem_pck.get_texto(794853) ||' ' || ds_submotivo_w, 
							clock_timestamp(), 
							null, 
							null, 
							nm_usuario_p 
						);
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
