CREATE OR REPLACE PROCEDURE alterar_tipo_conv_atend ( nm_usuario_p text, nr_atendimento_p bigint, cd_convenio_p bigint, cd_estabelecimento_p bigint, ie_tipo_convenio_p bigint, ie_alterou_p INOUT text) AS $body$
DECLARE

 
		 
ie_atualizar_w varchar(1);
ie_tipo_w bigint;			
 

BEGIN 
ie_alterou_p:= 'N';
 
select	coalesce(max(ie_atualizar_tipo_conv_atend),'S') 
into STRICT	ie_atualizar_w 
from	parametro_faturamento 
where	cd_estabelecimento = cd_estabelecimento_p;
 
if (ie_atualizar_w = 'S') then 
begin 
 
	select	max(ie_tipo_convenio) 
	into STRICT	ie_tipo_w 
	from	convenio 
	where	cd_convenio = cd_convenio_p;
 
	if (ie_tipo_w IS NOT NULL AND ie_tipo_w::text <> '') then 
		if (ie_tipo_convenio_p <> ie_tipo_w) then 
			update	atendimento_paciente 
			set ie_tipo_convenio = ie_tipo_w 
			where nr_atendimento = nr_atendimento_p;
			 
			commit;
			 
			ie_alterou_p:= 'S';
			 
			 
		end if;	
	end if;
end;
end if;
 
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
