SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_tipo_nf_pessoa ( nr_sequencia_p bigint, ie_tipo_nf_p text, cd_pessoa_fisica_p text, cd_cnpj_p text, nm_usuario_p text) AS $body$
DECLARE


cd_cnpj_emitente_w	varchar(14);
cd_cnpj_estab_w		varchar(14);
cd_pessoa_fisica_w	varchar(10);
cd_cnpj_w		varchar(14);
ie_tipo_nf_original_w	varchar(03);
qt_registro_w		bigint;
cd_estabelecimento_w	smallint;
cd_serie_nf_w		nota_fiscal.cd_serie_nf%type;
nr_nota_fiscal_w		varchar(255);
nr_sequencia_nf_w		bigint;


BEGIN

if (ie_tipo_nf_p in ('EN','EP','SD','SE','NC')) then
	begin
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		/*'Para este tipo de nota fiscal, não pode ser informado a pessoa física.');*/

		CALL wheb_mensagem_pck.exibir_mensagem_abort(198091);
	end if;
	end;
elsif (ie_tipo_nf_p in ('SF','EF')) then
	begin
	if (coalesce(cd_pessoa_fisica_p::text, '') = '') then
		/*'Para este tipo de nota fiscal, deve ser informado a pessoa física.');*/

		CALL wheb_mensagem_pck.exibir_mensagem_abort(198092);
	end if;
	end;
elsif (coalesce(ie_tipo_nf_p::text, '') = '') then
	/*'Favor informar um tipo para a nota fiscal.');*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(198093);
end if;


cd_cnpj_w		:= cd_cnpj_p;
cd_pessoa_fisica_w	:= cd_pessoa_fisica_p;

select	a.cd_cgc,
	b.ie_tipo_nota,
	b.cd_estabelecimento,
	b.cd_serie_nf,
	b.nr_nota_fiscal
into STRICT	cd_cnpj_emitente_w,
	ie_tipo_nf_original_w,
	cd_estabelecimento_w,
	cd_serie_nf_w,
	nr_nota_fiscal_w
from	estabelecimento a,
	nota_fiscal b
where	a.cd_estabelecimento	= b.cd_estabelecimento
and	b.nr_sequencia		= nr_sequencia_p;

if (ie_tipo_nf_p in ('EN','EP')) then
	cd_cnpj_emitente_w	:= cd_cnpj_w;
	cd_pessoa_fisica_w	:= '';
elsif (ie_tipo_nf_p = 'EF') then
	cd_cnpj_emitente_w	:= cd_cnpj_w;
	cd_cnpj_w		:= '';
elsif (ie_tipo_nf_p in ('SD','SE')) then
	cd_pessoa_fisica_w	:= '';
elsif (ie_tipo_nf_p = 'SF') then
	cd_cnpj_w		:= '';
end if;

select	count(*)
into STRICT	qt_registro_w
from	nota_fiscal
where	ie_situacao		= 1
and	cd_estabelecimento	= cd_estabelecimento_w
and	cd_cgc_emitente		= cd_cnpj_emitente_w
and	cd_serie_nf		= cd_serie_nf_w
and	nr_nota_fiscal		= nr_nota_fiscal_w
and	nr_sequencia		<> nr_sequencia_p;

if (qt_registro_w > 0) then
	/*'Já existe uma nota fiscal cadastrada com esse número para este fornecedor!');*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(198094);
end if;

select	coalesce(max(nr_sequencia_nf),0) + 1
into STRICT	nr_sequencia_nf_w
from	nota_fiscal
where	cd_estabelecimento		= cd_estabelecimento_w
and	cd_cgc_emitente		= cd_cnpj_emitente_w
and	cd_serie_nf		= cd_serie_nf_w
and	nr_nota_fiscal		= nr_nota_fiscal_w;

update	nota_fiscal
set	ie_tipo_nota	= ie_tipo_nf_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario 	= nm_usuario_p,
	cd_pessoa_fisica	= cd_pessoa_fisica_w,
	cd_cgc_emitente	= cd_cnpj_emitente_w,
	cd_cgc		= cd_cnpj_w,
	nr_sequencia_nf	= nr_sequencia_nf_w
where	nr_sequencia	= nr_sequencia_p;

insert into nota_fiscal_hist(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_nota,
		cd_evento,
		ds_historico)
	values (	nextval('nota_fiscal_hist_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_p,
		18,
		wheb_mensagem_pck.get_texto(315106,'IE_TIPO_NF_ORIGINAL_W='||IE_TIPO_NF_ORIGINAL_W||';IE_TIPO_NF_P='||IE_TIPO_NF_P));
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE alterar_tipo_nf_pessoa ( nr_sequencia_p bigint, ie_tipo_nf_p text, cd_pessoa_fisica_p text, cd_cnpj_p text, nm_usuario_p text) FROM PUBLIC;

