CREATE OR REPLACE PROCEDURE alterar_tipo_schem_doc (nr_sequencia_p bigint, nm_usuario_p text, ie_opcao_p text, ds_valores_p text) AS $body$
DECLARE


/*ie_opcao_p
1 - Traduzir titulo
2 - palavra chave
*/
BEGIN

if (ie_opcao_p = '1') then

	update	tipo_schem_doc
	set	ds_titulo_eng = ds_valores_p
	where	nr_sequencia = nr_sequencia_p;

elsif (ie_opcao_p = '2') then

	update	tipo_schem_doc
	set	ds_palavra_chave       	= ds_palavra_chave||';'||replace(ds_valores_p,',',';')
	where	nr_sequencia 		= nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
