CREATE OR REPLACE PROCEDURE alterar_titulo_pagar_escrit ( cd_ocorrencia_p text, ds_erro_p text, nr_seq_envio_p bigint, nr_titulo_p bigint, ie_opcao_p text, nr_pagamento_p text) AS $body$
DECLARE


/* ie_opcao_p

F	ocorrência do favorecido
R	ocorrência de retorno

*/
BEGIN

update titulo_pagar_escrit
set	cd_ocorrencia_ret_fav	= coalesce(CASE WHEN ie_opcao_p='F' THEN cd_ocorrencia_p  ELSE cd_ocorrencia_ret_fav END ,cd_ocorrencia_ret_fav),
	cd_ocorrencia_ret	= coalesce(CASE WHEN ie_opcao_p='R' THEN cd_ocorrencia_p  ELSE cd_ocorrencia_ret END ,cd_ocorrencia_ret),
	ds_erro			= coalesce(ds_erro_p,ds_erro),
	nr_pagamento		= coalesce(nr_pagamento_p,nr_pagamento)
where	nr_seq_escrit		= nr_seq_envio_p
and	nr_titulo		= nr_titulo_p;

commit;

end;
