CREATE OR REPLACE PROCEDURE alterar_trans_tit_bordero ( nr_bordero_p bigint, nr_seq_trans_financ_p bigint, nm_usuario_p text, ie_sem_transacao_p text) AS $body$
BEGIN

if (nr_bordero_p IS NOT NULL AND nr_bordero_p::text <> '') then
	begin
	update	bordero_tit_pagar
	set	nr_seq_trans_financ	= nr_seq_trans_financ_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_bordero		= nr_bordero_p
	and	coalesce(ie_selecionado,'N')	= 'S'
	and	((coalesce(ie_sem_transacao_p,'N')	= 'S'
		and	coalesce(nr_seq_trans_financ::text, '') = '')
		or (coalesce(ie_sem_transacao_p,'N') = 'N'));
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
