CREATE OR REPLACE PROCEDURE alterar_tx_part_proc ( pr_procedimento_p bigint, nr_seq_procedimento_p bigint, nr_seq_partic_p bigint, nr_seq_audit_propaci_p bigint, nr_seq_motivo_audit_p bigint) AS $body$
DECLARE


nr_seq_motivo_audit_w	bigint;


BEGIN

if (nr_seq_procedimento_p > 0) and (nr_seq_partic_p > 0) then

	update  procedimento_participante
	set 	pr_faturar = coalesce(pr_procedimento_p,0)
	where   nr_sequencia = nr_seq_procedimento_p
	and     nr_seq_partic = nr_seq_partic_p;

	if (coalesce(nr_seq_motivo_audit_p,0) <> 0) then
		nr_seq_motivo_audit_w	:= nr_seq_motivo_audit_p;
	else
		nr_seq_motivo_audit_w	:= null;
	end if;

	update	auditoria_propaci
	set	ie_tipo_auditoria = 'V',
		nr_seq_motivo = nr_seq_motivo_audit_w
	where 	nr_sequencia = nr_seq_audit_propaci_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
