CREATE OR REPLACE PROCEDURE alterar_usuario_status_senha ( nm_usuario_p text, nm_usuario_alt_p text, nr_seq_status_p bigint) AS $body$
DECLARE


dt_fim_status_w 	timestamp;
nr_sequencia_w	bigint;


BEGIN

select	max(dt_inicio_status)
into STRICT	dt_fim_status_w
from	usuario_status_senha
where	nm_usuario = nm_usuario_alt_p;

if (nm_usuario_alt_p IS NOT NULL AND nm_usuario_alt_p::text <> '') and (nr_seq_status_p IS NOT NULL AND nr_seq_status_p::text <> '') then
	begin

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	usuario_status_senha
	where	nm_usuario = nm_usuario_alt_p;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then

		begin

		update 	usuario_status_senha
		set	dt_fim_status = clock_timestamp()
		where	nr_sequencia = nr_sequencia_w;

		end;

	end if;

	insert into usuario_status_senha(	nr_sequencia,
					nm_usuario_nrec,
					dt_inicio_status,
					dt_fim_status,
					nm_usuario,
					nr_seq_status)
				values (	nextval('usuario_status_senha_seq'),
					nm_usuario_p,
					clock_timestamp(),
					null,
					nm_usuario_alt_p,
					nr_seq_status_p);
	end;
end if;

commit;

end;
$body$
