CREATE OR REPLACE PROCEDURE alterar_valor_campo ( nm_tabela_p text, ds_campo_p text, ds_campo_restricao_p text, ds_valor_p text, ds_valor_restricao_p text, ie_campo_data_p text, ie_opcao_p text) AS $body$
DECLARE


/*	ie_opcao_p
	A - Atualizar
	D - Desfazer
	*/
ds_sql_w		varchar(2000);
ds_parametros_w	varchar(2000);
ds_sep_bv_w	varchar(10);



BEGIN
ds_parametros_w	:= '';
ds_sep_bv_w	:= obter_separador_bv;

if (ie_opcao_p = 'A') then
	begin
	if (ie_campo_data_p = 'S') then
		begin
		ds_sql_w	:=
			' update	' || nm_tabela_p ||
			' set	' || ds_campo_p || '	= sysdate ' ||
			' where	' || ds_campo_restricao_p||'	= :ds_valor_restricao ';
		end;
	else
		begin
		ds_sql_w	:=
			' update	' || nm_tabela_p ||
			' set	' || ds_campo_p || '	= :ds_valor ' ||
			' where	' || ds_campo_restricao_p||'	= :ds_valor_restricao ';
		ds_parametros_w	:= 'ds_valor=' || ds_valor_p || ds_sep_bv_w;
		end;
	end if;
	end;

elsif (ie_opcao_p = 'D') then
	begin
	ds_sql_w	:=
		' update	' || nm_tabela_p ||
		' set	' || ds_campo_p || '	= null ' ||
		' where	' || ds_campo_restricao_p||'	= :ds_valor_restricao ';
	end;
end if;

ds_parametros_w	:= ds_parametros_w || 'ds_valor_restricao=' || ds_valor_restricao_p || ds_sep_bv_w;

CALL exec_sql_dinamico_bv('', ds_sql_w, ds_parametros_w);

commit;

end;
$body$
