SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_valor_informado ( nr_sequencia_p bigint, ie_valor_inf_p text, nm_usuario_p text, cd_convenio_p bigint, ie_tipo_p bigint) AS $body$
BEGIN
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
	if (ie_tipo_p = 1) then 
		update	procedimento_paciente 
		set	ie_valor_informado	= CASE WHEN ie_valor_inf_p='S' THEN  'N'  ELSE 'S' END , 
			nm_usuario	= nm_usuario_p, 
			dt_atualizacao	= clock_timestamp()			 
		where	nr_sequencia	= nr_sequencia_p;
		 
		CALL atualiza_preco_procedimento(nr_sequencia_p, cd_convenio_p, nm_usuario_p);
	end if;
	 
	if (ie_tipo_p = 2) then 
		update	material_atend_paciente 
		set	ie_valor_informado	= CASE WHEN ie_valor_inf_p='S' THEN  'N'  ELSE 'S' END , 
			nm_usuario	= nm_usuario_p, 
			dt_atualizacao	= clock_timestamp() 
		where	nr_sequencia	= nr_sequencia_p;
 
		CALL atualiza_preco_material(nr_sequencia_p, nm_usuario_p);
	end if;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
