CREATE OR REPLACE PROCEDURE alterar_valor_param_regra_func (nr_seq_regra_p bigint, ie_parametro_p text, ie_novo_valor_p text, nm_usuario_p text) AS $body$
BEGIN
if (nr_seq_regra_p IS NOT NULL AND nr_seq_regra_p::text <> '') and (ie_parametro_p IS NOT NULL AND ie_parametro_p::text <> '') and (ie_novo_valor_p IS NOT NULL AND ie_novo_valor_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	if (ie_novo_valor_p = 'S') then

		update	funcao_regra
		set	ds_parametro		= replace(ds_parametro,ie_parametro_p||'=N',ie_parametro_p||'=S'),
			nm_usuario		= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia		= nr_seq_regra_p;

	elsif (ie_novo_valor_p = 'N') then

		update	funcao_regra
		set	ds_parametro		= replace(ds_parametro,ie_parametro_p||'=S',ie_parametro_p||'=N'),
			nm_usuario		= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia		= nr_seq_regra_p;

	end if;

end if;

commit;

end;
$body$
