CREATE OR REPLACE PROCEDURE alterar_valor_projeto_nf_item ( nr_sequencia_p bigint, nr_item_nf_p bigint, vl_projeto_p bigint, ie_operacao_p text, nm_usuario_p text) AS $body$
BEGIN
/*
A - Alterar valor
E  - Excluir valor
*/
if (ie_operacao_p = 'A') then
	begin
	update	nota_fiscal_item
	set	vl_projeto	= vl_projeto_p,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p
	and 	nr_item_nf	= nr_item_nf_p;
	end;
elsif (ie_operacao_p = 'E') then
	begin
	update	nota_fiscal_item
	set	vl_projeto	 = NULL,
		nr_seq_proj_rec	 = NULL,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p
	and 	nr_item_nf	= nr_item_nf_p;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
