SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_valor_unit_audit_ext ( nr_sequencia_p bigint, vl_unitario_p bigint, nr_seq_motivo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
ie_altera_item_repasse_w	varchar(1) := 'S';
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.Get_Cd_estabelecimento;

C01 CURSOR FOR
	SELECT	c.nr_sequencia
	from	material_atend_paciente c,
		auditoria_externa_item b,
		auditoria_externa a
	where	a.nr_sequencia = b.nr_seq_audit_ext
	and	b.nr_seq_item = c.nr_sequencia
	and	a.nr_sequencia = nr_sequencia_p
	and	not exists (	SELECT	1
				from	material_repasse x
				where	x.nr_seq_material = c.nr_sequencia
				and	coalesce(ie_altera_item_repasse_w,'S') = 'N');

BEGIN

ie_altera_item_repasse_w := obter_param_usuario(1116, 189, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_altera_item_repasse_w);

if (coalesce(nr_sequencia_p,0) > 0) then

	open C01;
	loop
	fetch C01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	material_atend_paciente
		set	vl_material = coalesce(vl_unitario_p,0) * qt_material,
			vl_unitario = coalesce(vl_unitario_p,0),
			ie_valor_informado = 'S',
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia = nr_sequencia_w;

		if (coalesce(nr_seq_motivo_p,0) > 0) then
			update	auditoria_externa
			set	nr_seq_motivo = nr_seq_motivo_p
			where	nr_sequencia = nr_sequencia_p;
		end if;

		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
