SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE alterar_vencimento_tit_rec ( nr_titulo_p bigint, dt_vencimento_p timestamp, cd_motivo_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w				integer;
dt_venc_atual_w				timestamp;
dt_emissao_w				timestamp;


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') and (dt_vencimento_p IS NOT NULL AND dt_vencimento_p::text <> '') and (cd_motivo_p IS NOT NULL AND cd_motivo_p::text <> '') then

	select	coalesce(max(nr_sequencia),0) + 1
	into STRICT	nr_sequencia_w
	from	alteracao_vencimento
	where	nr_titulo = nr_titulo_p;

	select	max(dt_emissao),
		max(dt_pagamento_previsto)
	into STRICT	dt_emissao_w,
		dt_venc_atual_w
	from	titulo_receber
	where	nr_titulo	= nr_titulo_p;

	insert	into	alteracao_vencimento(nr_titulo,
			nr_sequencia,
			dt_anterior,
			dt_vencimento,
			cd_motivo,
			dt_atualizacao,
			nm_usuario,
			dt_alteracao,
			ds_observacao)
		values (nr_titulo_p,
			nr_sequencia_w,
			dt_venc_atual_w,
			dt_vencimento_p,
			cd_motivo_p,
			clock_timestamp(),
			nm_usuario_p,
			trunc(clock_timestamp(),'dd'),
			ds_observacao_p);

	if (dt_vencimento_p < dt_emissao_w) then
		/*O vencimento não pode ser menor que a data de emissão do título!*/

		CALL wheb_mensagem_pck.exibir_mensagem_abort(238657);
	else
		update	titulo_receber
		set	dt_pagamento_previsto	= dt_vencimento_p
		where	nr_titulo		= nr_titulo_p;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
