CREATE OR REPLACE PROCEDURE alterar_venc_nota_fiscal ( nr_seq_nota_p bigint, nr_titulo_p bigint, dt_venc_origem_p timestamp, dt_vencimento_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_situacao_w		varchar(15);


BEGIN

if (coalesce(nr_titulo_p,0) > 0) then
	begin

	select	ie_situacao
	into STRICT	ie_situacao_w
	from	titulo_pagar
	where	nr_titulo = nr_titulo_p;

	if (ie_situacao_w <> 'A') then
		--(-20011,'O título deste vencimento não está aberto! Não será permitido alterar o vencimento');
		CALL wheb_mensagem_pck.exibir_mensagem_abort(177666);
	end if;
	end;
end if;

update	nota_fiscal_venc
set	dt_vencimento = trunc(dt_vencimento_p)
where	nr_sequencia = nr_seq_nota_p
and	trunc(dt_vencimento) = trunc(dt_venc_origem_p);

CALL gerar_historico_nota_fiscal(nr_seq_nota_p, nm_usuario_p, '41',
	WHEB_MENSAGEM_PCK.get_texto(457439,'dt_venc_origem_p='|| to_char(dt_venc_origem_p,'dd/mm/yyyy') ||';dt_vencimento_p='|| to_char(dt_vencimento_p,'dd/mm/yyyy')));

if (coalesce(nr_titulo_p,0) > 0) then
	begin

	CALL alterar_venc_titulo(nr_titulo_p,
		dt_vencimento_p,
		'CP',null,null,null,
		cd_estabelecimento_p,
		null,null,nm_usuario_p,
		OBTER_DESC_EXPRESSAO(727714));


	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
