CREATE OR REPLACE PROCEDURE alterar_venc_tit_rec_log ( nr_titulo_p bigint, dt_vencimento_p timestamp, nm_usuario_p text) AS $body$
DECLARE


dt_vencimento_w		timestamp;
dt_venc_nova_w		varchar(50);
dt_venc_antiga_w		varchar(50);


BEGIN

select	dt_vencimento
into STRICT	dt_vencimento_w
from	titulo_receber
where	nr_titulo 	= nr_titulo_p;

update	titulo_receber
set	dt_vencimento	= dt_vencimento_p
where	nr_titulo	= nr_titulo_p;

dt_venc_nova_w		:= dt_vencimento_p || ' - 1843398';
dt_venc_antiga_w		:= dt_vencimento_w || ' - 1843398';

CALL gerar_titulo_receber_alt(nr_titulo_p, nm_usuario_p, dt_venc_nova_w, dt_venc_antiga_w, 'DT_VENCIMENTO');

commit;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
