CREATE OR REPLACE PROCEDURE alterar_vl_juro_multa_alt_venc (nr_titulo_p bigint, nr_seq_alt_venc_p bigint, vl_juros_p bigint, vl_multa_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Alterar valor de juros e multa da alteração de vencimento e do saldo do título
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/nr_seq_alt_venc_w	bigint;


BEGIN
select	max(nr_sequencia)
into STRICT	nr_seq_alt_venc_w
from	alteracao_vencimento
where	nr_titulo	= nr_titulo_p;

if (nr_seq_alt_venc_w <> nr_seq_alt_venc_p) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(220264);
end if;

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') and (nr_seq_alt_venc_p IS NOT NULL AND nr_seq_alt_venc_p::text <> '') then
	update	alteracao_vencimento
	set	vl_juros		= coalesce(vl_juros_p,vl_juros),
		vl_multa		= coalesce(vl_multa_p,vl_multa),
		ie_juros_multa_usuario	= 'S'
	where	nr_sequencia	= nr_seq_alt_venc_p
	and	nr_titulo	= nr_titulo_p;

	update	titulo_receber
	set	vl_saldo_juros	= coalesce(vl_juros_p,vl_saldo_juros),
		vl_saldo_multa	= coalesce(vl_multa_p,vl_saldo_multa)
	where	nr_titulo	= nr_titulo_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
