CREATE OR REPLACE PROCEDURE alterar_vl_unit_oc_atendimento ( cd_estabelecimento_p bigint, nr_atendimento_p bigint, cd_fornecedor_p text, cd_material_p bigint, vl_unitario_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_ordem_compra_w		bigint;


BEGIN 
 
select	obter_ordem_atend_consignado(cd_estabelecimento_p, cd_fornecedor_p, 0, 0, nr_atendimento_p, 0,'G') 
into STRICT	nr_ordem_compra_w
;
 
if (nr_ordem_compra_w > 0) and (cd_material_p > 0) then 
	 
	update	ordem_compra_item 
	set	vl_unitario_material		= coalesce(vl_unitario_p,0), 
		vl_total_item		= coalesce(vl_unitario_p,0) * qt_material 
	where	nr_ordem_compra		= nr_ordem_compra_w 
	and	cd_material		= cd_material_p;
	 
	CALL inserir_historico_ordem_compra( 
			nr_ordem_compra_w, 
			'S', 
			WHEB_MENSAGEM_PCK.get_texto(297155), 
			WHEB_MENSAGEM_PCK.get_texto(297158, 
						'CD_MATERIAL_P='||CD_MATERIAL_P|| 
						';VL_UNITARIO_P='||campo_mascara_virgula_casas(coalesce(vl_unitario_p,0),4)), 
			nm_usuario_p);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
