CREATE OR REPLACE PROCEDURE altera_agenda_pedido_agend ( nr_sequencia_p bigint, cd_agenda_p bigint, nr_atendimento_p bigint, ie_status_espera_p text, nm_usuario_p text) AS $body$
DECLARE

					
ie_urgente_w   agenda_lista_espera.ie_urgente%type;


BEGIN

	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '')then
		select 	coalesce(max(a.ie_urgente),'N')
		into STRICT    	ie_urgente_w
		from 	agenda_paciente_classif a,
		       	agenda_lista_espera b
		 where  	a.nr_sequencia = b.nr_seq_classif_agenda
		 and 	b.nr_sequencia = nr_sequencia_p;
	
		update 	agenda_lista_espera
		set	cd_agenda  		= cd_agenda_p,
			nr_atendimento 		= nr_atendimento_p,
                        		ie_status_espera		= ie_status_espera_p,
			dt_atualizacao 		= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			ie_urgente		= ie_urgente_w
		where	nr_sequencia		= nr_sequencia_p;
		commit;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
