CREATE OR REPLACE PROCEDURE altera_anvisa_material_estab ( cd_material_p bigint, nr_registro_anvisa_p text, dt_validade_reg_anvisa_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	begin

	update	material_estab
	set		nr_registro_anvisa 		= nr_registro_anvisa_p,
			dt_validade_reg_anvisa 	= dt_validade_reg_anvisa_p,
			ie_vigente_anvisa 		= substr(coalesce(obter_exigencia_anvisa_mat(cd_estabelecimento,cd_material_p,'VI'),'N'),1,1)
	where	cd_material 			= cd_material_p
	and		cd_estabelecimento 		= cd_estabelecimento_p;

	commit;
	end;
end if;

end;
$body$
