SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE altera_aprovacao_lib ( nr_repasse_terceiro_p bigint, ie_desfazer_aprovacao_p text, nm_usuario_p text, qt_aprovacao_p INOUT bigint) AS $body$
DECLARE



/*
D - Opcao 'Desfazer aprovacao lib'
A - Opcao 'Aprovar repasse lib'
*/
BEGIN

if (upper(ie_desfazer_aprovacao_p) = 'D') then
	begin

	update	repasse_terceiro_aprovacao
	set		dt_aprovacao	 = NULL,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
	where	nr_repasse_terceiro	= nr_repasse_terceiro_p
	and		nm_usuario_aprovacao	= nm_usuario_p;

	select	count(*)
	into STRICT	qt_aprovacao_p
	from	repasse_terceiro_aprovacao
	where	nr_repasse_terceiro	= nr_repasse_terceiro_p
	and		(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');

	end;
elsif (upper(ie_desfazer_aprovacao_p) = 'A') then
	begin

	update	repasse_terceiro_aprovacao
	set		dt_aprovacao	= clock_timestamp(),
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
	where	nr_repasse_terceiro	= nr_repasse_terceiro_p
	and		nm_usuario_aprovacao	= nm_usuario_p;

	select	count(*)
	into STRICT	qt_aprovacao_p
	from	repasse_terceiro_aprovacao
	where	nr_repasse_terceiro	= nr_repasse_terceiro_p
	and		coalesce(dt_aprovacao::text, '') = '';

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
