CREATE OR REPLACE PROCEDURE altera_atend_marc_agequi_pep ( nr_seq_pend_agenda_p bigint, nr_seq_atend_new_p bigint, nr_seq_atend_old_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_agenda_w			paciente_atendimento.dt_real%type;
cd_estab_local_w	paciente_atendimento.cd_estabelecimento%type;


BEGIN

update	agenda_quimio_marcacao
set		nr_seq_atendimento	=	nr_seq_atend_new_p,
		dt_atualizacao		=	clock_timestamp(),
		nm_usuario 			= 	nm_usuario_p
where	nr_seq_pend_agenda 	= 	nr_seq_pend_agenda_p
and		nr_seq_atendimento 	= 	nr_seq_atend_old_p;

update	agenda_quimio
set		nr_seq_atendimento 	= 	nr_seq_atend_new_p,
		dt_atualizacao		=	clock_timestamp(),
		nm_usuario 			= 	nm_usuario_p
where	nr_seq_pend_agenda 	=	nr_seq_pend_agenda_p
and		nr_seq_atendimento 	= 	nr_seq_atend_old_p;

select	max(dt_real),
		max(cd_estabelecimento)
into STRICT	dt_agenda_w,
		cd_estab_local_w
from 	paciente_atendimento
where	nr_seq_pend_agenda	= 	nr_seq_pend_agenda_p
and		nr_seq_atendimento 	= 	nr_seq_atend_old_p;

update	paciente_atendimento
set		dt_real				= 	dt_agenda_w,
		nm_usuario			= 	nm_usuario_p,
		dt_atualizacao		= 	clock_timestamp(),
		cd_estabelecimento 	= 	cd_estab_local_w
where	nr_seq_pend_agenda	= 	nr_seq_pend_agenda_p
and		nr_seq_atendimento 	= 	nr_seq_atend_new_p;

commit;

end;
$body$
